import React from "react";
import { Observable, ObservableValues } from "./observable";
declare type Mapping = {
    [key: string]: Observable<any>;
};
declare type InjectedProps<M extends Mapping> = ObservableValues<M>;
declare type HocProps<P extends InjectedProps<M>, M extends Mapping> = Pick<P, Exclude<keyof P, keyof M>>;
export declare const withObservables: <P extends ObservableValues<M>, M extends Mapping>(Component: React.ComponentType<P>, mapping: (ownProps: Pick<P, Exclude<keyof P, keyof M>>) => M) => React.ComponentType<Pick<P, Exclude<keyof P, keyof M>>>;
export {};
